// SetUtilities removes all previously related items of the
// collection_item replacing them completely with the passed
// in related items, optionally inserting them as new records.
// Sets o.R.CollectionItem's Utilities accordingly.
// Replaces o.R.Utilities with related.
// Sets related.R.CollectionItem's Utilities accordingly.
func (o *CollectionItem) SetUtilities(exec boil.Executor, insert bool, related ...*Utility) error {
	query := "update \"utility\" set \"collection_item_id\" = null where \"collection_item_id\" = $1"
	values := []interface{}{o.ID}
	if boil.DebugMode {
		fmt.Fprintln(boil.DebugWriter, query)
		fmt.Fprintln(boil.DebugWriter, values)
	}
	_, err := exec.Exec(query, values...)
	if err != nil {
		return errors.Wrap(err, "failed to remove relationships before set")
	}

	if o.R != nil {
		for _, rel := range o.R.Utilities {
			queries.SetScanner(&rel.CollectionItemID, nil)
			if rel.R == nil {
				continue
			}

			rel.R.CollectionItem = nil
		}

		o.R.Utilities = nil
	}
	return o.AddUtilities(exec, insert, related...)
}

// RemoveUtilities relationships from objects passed in.
// Removes related items from R.Utilities (uses pointer comparison, removal does not keep order)
// Sets related.R.CollectionItem.
func (o *CollectionItem) RemoveUtilities(exec boil.Executor, related ...*Utility) error {
	if len(related) == 0 {
		return nil
	}

	var err error
	for _, rel := range related {
		queries.SetScanner(&rel.CollectionItemID, nil)
		if rel.R != nil {
			rel.R.CollectionItem = nil
		}
		if _, err = rel.Update(exec, boil.Whitelist("collection_item_id")); err != nil {
			return err
		}
	}
	if o.R == nil {
		return nil
	}

	for _, rel := range related {
		for i, ri := range o.R.Utilities {
			if rel != ri {
				continue
			}

			ln := len(o.R.Utilities)
			if ln > 1 && i < ln-1 {
				o.R.Utilities[i] = o.R.Utilities[ln-1]
			}
			o.R.Utilities = o.R.Utilities[:ln-1]
			break
		}
	}

	return nil
}

