// FindTelegramPlayer retrieves a single record by ID with an executor.
// If selectCols is empty Find will return all columns.
func FindTelegramPlayer(exec boil.Executor, iD string, selectCols ...string) (*TelegramPlayer, error) {
	telegramPlayerObj := &TelegramPlayer{}

	sel := "*"
	if len(selectCols) > 0 {
		sel = strings.Join(strmangle.IdentQuoteSlice(dialect.LQ, dialect.RQ, selectCols), ",")
	}
	query := fmt.Sprintf(
		"select %s from \"telegram_players\" where \"id\"=$1", sel,
	)

	q := queries.Raw(query, iD)

	err := q.Bind(nil, exec, telegramPlayerObj)
	if err != nil {
		if errors.Cause(err) == sql.ErrNoRows {
			return nil, sql.ErrNoRows
		}
		return nil, errors.Wrap(err, "boiler: unable to select from telegram_players")
	}

	if err = telegramPlayerObj.doAfterSelectHooks(exec); err != nil {
		return telegramPlayerObj, err
	}

	return telegramPlayerObj, nil
}

